import React, { useState, useEffect } from 'react';
import Transaction from './Transaction';







const Receipt = (props) => {

  const [tnx, setTnx] = useState([]);

  useEffect(() => {
    fetch('http://localhost:4000/transcation_details')
      .then(response => response.json())
      .then(data => {
        setTnx(data)
        // Transaction(data)

        console.log(data)
      })
      .catch(error => console.error(error));
  }, []);

  // const { sourceAccount, destinationAccount, amount, transactionhash, blockhash } = props;
  console.log(props.receipts);
  // className='receipt'
  return (
    <div >
      <div>


        {tnx.map(tnxId => (
          <div key={tnxId._id}>
            {/* {tnxId.destination_account} */}


            <p>Transaction Hash: {tnx.tnx_hash}</p>
            <p>Block Hash: {tnx.block_hash}</p>
            <p>Source Account: {tnx.source_account}</p>
            <p>Destination Account: {tnx.destination_account}</p>
            <p>Amount: {tnx.amount}</p>
            <p>Gas Used: {tnx.gas_used}</p>
          </div>
        ))}


      </div>

      <div>
        <ul>

        </ul>
      </div>
    </div>


  );

};

export default Receipt;
