import React, { useEffect, useState, Component } from 'react';
import Receipt from './Receipt';
import data from '../model/data.json';
import Select from 'react-select'




const Transaction = () => {
  const [amount, setAmount] = useState('');
  const [showReceipt, setShowReceipt] = useState(false);
  var [ownerBalance, setOwnerBalance] = useState(data.ownerbalance)
  var sourceAccount = 'Account A';
  const [selectedOption, setSelectedOption] = useState(null); // initialize state to null
  const [destinationAccount, setDestinationAccount] = useState('initial-value');
  const inputAmount = document.getElementById('amount_check');
  const [receipts, setReceipts] = useState([]);
  const transactionhash = "fdc3c31435f7f2f557bfd82";
  const blockhash = "Rgsj1AKVnpJYhHz4rN";


  const [walletData, setWalletData] = useState({});
  const [ownerWallet, setOwnerWallet] = useState({});

  useEffect(() => {
    fetch('http://localhost:4000/json')
      .then(response => response.json())
      .then(data => setWalletData(data))
      .catch(error => console.error(error));
  }, []);


  useEffect(() => {
    fetch('http://localhost:4000/owner_data')
      .then(response => response.json())
      .then(data => setOwnerWallet(data))
      .catch(error => console.error(error));
  });






  const options = walletData.map(wallet => ({
    value: wallet.walletaddress,
    label: wallet.walletaddress
  }));

  // function to handle adding a new receipt
  const addReceipt = (sourceAccount, destinationAccount, amount) => {
    const newReceipt = {
      sourceAccount: sourceAccount,
      destinationAccount: destinationAccount,
      amount: amount
    };
    setReceipts([...receipts, newReceipt]);
  }


  const handleSubmit = (event) => {
    event.preventDefault(); // prevent default form submission behavior
    if (selectedOption === null || inputAmount.value === null || inputAmount.value === 0 || inputAmount.value === "" || inputAmount.value === "0") {
      if (selectedOption != null) {
        alert('Please enter how much ETH you want to send.')
      } else {
        alert('Please select wallet address to send ETH.')
      }

    } else {

      // submit the form or perform any other necessary action
      alert('Are you sure to send ' + inputAmount.value + ' ETH')
      console.log(selectedOption.value);
      setAmount(inputAmount.value);
      setDestinationAccount(selectedOption.value);
      var OwnerCurrentBalance = ownerBalance - inputAmount.value;


      //Get Index and update value of JSON data  ------INDEX Code START---------
      const selectedIndex = options.findIndex((item) => item.value === selectedOption.value);
      console.log(selectedIndex);
      console.log(OwnerCurrentBalance);

      setOwnerBalance(OwnerCurrentBalance);
      data['wallets'].at(selectedIndex).walletbalance = parseFloat(data['wallets'].at(selectedIndex).walletbalance) + parseFloat(inputAmount.value);
      console.log(data['wallets'].at(selectedIndex).walletbalance);
      //  ------ INDEX Code END ---------
      addReceipt(sourceAccount, selectedOption.value, inputAmount.value);
      console.log(receipts)
      setShowReceipt(true);
    }

  }


  // handle select option change
  const handleSelectChange = (selectedOption) => {
    setSelectedOption(selectedOption); // update state when an option is selected

  }



  return (

    <div className='transaction'>
      <h1>Transfer</h1>
      <form onSubmit={handleSubmit}  >
        <fieldset>
          <div className="App">

          </div>

          <label>

            <div style={{ display: 'flex', flexDirection: 'row', alignItems: 'center' }}>
              <div >
                <p><b> Wallet Owner :    </b></p>
              </div>
              <div style={{ paddingLeft: '20px' }}>
                <p><b>  {sourceAccount = data.owneraddress} </b></p>
              </div>

            </div>

            <div style={{ display: 'flex', flexDirection: 'row', alignItems: 'center' }}>
              <div  >
                <p><b>  Owner Balance : </b></p>
              </div>

              <div id='ownerBalance' style={{ paddingLeft: '20px' }} >
                <p><b>  {ownerBalance = ownerBalance} </b></p>

              </div>

              {/* 
              <div style={{ paddingLeft: '20px' }}>
                {walletData ? (
                  <Select
                    options={walletData.wallets.map(wallet => ({
                      value: wallet.walletaddress,
                      label: wallet.walletaddress
                    }))}
                    onChange={handleSelectChange}
                    value={selectedOption}
                    placeholder="Select wallet address to sent ETH"
                  />
                ) : (
                  <p>No data yet</p>
                )}
              </div> */}


              {/* 
              <div>

                <ul>
                  {walletData.map(wallet => (
                    <li key={wallet.walletaddress}>
                      <span>Wallet Address: {wallet.walletaddress}</span>
                      <span>Wallet Balance: {wallet.walletbalance}</span>
                    </li>
                  ))}
                </ul>

              </div> */}

            </div>


            <div style={{ display: 'flex', flexDirection: 'row', alignItems: 'center' }}>
              <div >
                <p><b> Reciver Address    </b></p>
              </div>
              <div style={{ paddingLeft: '20px' }}>
                <Select options={options}
                  onChange={handleSelectChange}
                  value={selectedOption}
                  placeholder="Select wallet address to sent ETH"
                />
              </div>

            </div>
          </label>
          <label>



            <div style={{ display: 'flex', flexDirection: 'row', alignItems: 'center' }}>
              <div >
                <p><b> Amount in Ether </b></p>
              </div>
              <div style={{ paddingLeft: '20px' }}>
                <input id='amount_check' name="Amount" type="number" style={{ height: '35px' }} />
              </div>
            </div>

          </label>

        </fieldset>

        <button type="submit" className="button">Submit</button>


      </form>

      <div>
        {showReceipt && <Receipt receipts={receipts} sourceAccount={sourceAccount} destinationAccount={destinationAccount} amount={amount} transactionhash={transactionhash} blockhash={blockhash} />}
      </div>

    </div >

  );
};

export default Transaction;
