import React, { useState, useEffect } from 'react';
import Transaction from './Transaction';

const Receipt = (props) => {

  const { sourceAccount, destinationAccount, amount, transactionhash, blockhash } = props;
  console.log(props.receipts);

  const [tnx, setTnx] = useState([]);

  useEffect(() => {
    fetch('http://localhost:4000/transcation_details')
      .then(response => response.json())
      .then(data => {
        setTnx(data)
        console.log(data)
      })
      .catch(error => console.error(error));
  }, []);





  return (
    <div className='receipt'>
      <h2><b>Transaction Receipt</b></h2>
      <p><b>Transaction Hash: </b>{transactionhash}</p>
      <p><b>Block Hash:</b> {blockhash}  </p>
      <p><b>Block number:</b> 789</p>
      <p><b>Source Account:</b> {sourceAccount} </p>
      <p><b>Destination Account:</b> {destinationAccount} </p>
      <p><b>Gas Used:</b> 1100 </p>
      <p><b>Amount:</b> {amount}</p>
    </div>
  );

};

export default Receipt;
